    private void mostrarDatosProyectos() {
    String[] titulos = {"Id", "Nombre", "Fecha Inicio", "Fecha Fin", "Id Ingeniero", "Id Departamento"};
    String[] registros = new String[6];
    DefaultTableModel modelo = new DefaultTableModel(null, titulos);
    String SQL = "SELECT * FROM proyectos";

    try {
        Statement st = con.createStatement();
        ResultSet rs = st.executeQuery(SQL);
        while (rs.next()) {
            registros[0] = rs.getString("idProyecto");
            registros[1] = rs.getString("nombre");
            registros[2] = rs.getString("fechaInicio");
            registros[3] = rs.getString("fechaFin");
            registros[4] = rs.getString("idIngeniero");
            registros[5] = rs.getString("idDepartamento");
            modelo.addRow(registros);
        }
        tablaProyectos.setModel(modelo);
    } catch (SQLException e) {
        JOptionPane.showMessageDialog(null, "Error: " + e.getMessage());
    }
}

    private void eliminarProyecto() {
        int filaSeleccionada = tablaProyectos.getSelectedRow();
        try {
            String SQL = "DELETE FROM proyectos WHERE idProyecto=" + tablaProyectos.getValueAt(filaSeleccionada, 0);
            Statement st = con.createStatement();
            int n = st.executeUpdate(SQL);

            if (n >= 0) {
                JOptionPane.showMessageDialog(null, "Registro eliminado");
            }
            mostrarDatosProyectos();
        } catch (SQLException e) {
            JOptionPane.showMessageDialog(null, "Error al eliminar registro: " + e.getMessage());
        }
    }

    private void actualizarProyecto() {
        try {
            String SQL = "UPDATE proyectos SET nombre=?, fechaInicio=?, fechaFin=?, idIngeniero=?, idDepartamento=? WHERE idProyecto=?";
            int filaSeleccionada = tablaProyectos.getSelectedRow();
            String dao = (String) tablaProyectos.getValueAt(filaSeleccionada, 0);

            PreparedStatement pst = con.prepareStatement(SQL);
            pst.setString(1, txtNombreProyecto.getText());
            pst.setDate(2, Date.valueOf(txtFechaInicio.getText()));
            pst.setDate(3, Date.valueOf(txtFechaFin.getText()));
            pst.setInt(4, Integer.parseInt(txtIdIngeniero.getText()));
            pst.setInt(5, Integer.parseInt(txtIdDepartamento.getText()));
            pst.setString(6, dao);
            pst.execute();

            JOptionPane.showMessageDialog(null, "Registro editado exitosamente");
            mostrarDatosProyectos();
        } catch (SQLException e) {
            JOptionPane.showMessageDialog(null, "Error en la edici√≥n: " + e.getMessage());
        }
    }

    private void guardarProyecto() {
        try {
            String SQL = "INSERT INTO proyectos (nombre, fechaInicio, fechaFin, idIngeniero, idDepartamento) VALUES (?, ?, ?, ?, ?)";
            PreparedStatement pst = con.prepareStatement(SQL);
            pst.setString(1, txtNombreProyecto.getText());
            pst.setDate(2, Date.valueOf(txtFechaInicio.getText()));
            pst.setDate(3, Date.valueOf(txtFechaFin.getText()));
            pst.setInt(4, Integer.parseInt(txtIdIngeniero.getText()));
            pst.setInt(5, Integer.parseInt(txtIdDepartamento.getText()));
            pst.execute();

            JOptionPane.showMessageDialog(null, "Registro exitoso");
            mostrarDatosProyectos();
        } catch (SQLException e) {
            JOptionPane.showMessageDialog(null, "Error en el registro: " + e.getMessage());
        }
    }

